import React from 'react';
import Head from 'next/head';
import { withRouter } from 'next/router';
import { useQuery } from '@apollo/react-hooks';
import { GET_PROGRAM } from '../../graphQL/queries';
import Layout from '../../components/Layout/Layout';
import { Spinner } from '../../components/Spinner/Spinner';

import SchoolBanner from '../../components/SchoolBanner/SchoolBanner';
import ProgramInfoBanner from '../../components/ProgramInfoBanner/ProgramInfoBanner';
import ProgramInfo from '../../components/ProgramInfo/ProgramInfo';

const calcDeliveryModeColor = (color1, color2, percent) => {
  const parseHex = hex =>
    hex.length === 4
      ? hex
          .slice(1, 4)
          .split('')
          .map(el => el + el)
      : [hex.substr(1, 2), hex.substr(3, 2), hex.substr(5, 2)];
  console.log(parseHex('#013'));
  console.log(parseHex('#112233'));
};

const renderBody = ({
  name,
  costPerCredit,
  programUrl,
  programDescription,
  deliveryMode,
  school,
}) => {
  calcDeliveryModeColor('#FF0000', '#0000FF', 0.5);

  return (
    <div>
      <Head>
        <title>{name}</title>
      </Head>
      <SchoolBanner
        school={school}
        name={name}
        costPerCredit={costPerCredit}
        deliveryMode={deliveryMode}
      />
      <ProgramInfoBanner />
      <ProgramInfo
        programDescription={programDescription}
        schoolDescription={school.schoolDescription}
        schoolName={school.name}
      />
    </div>
  );
};

const SchoolPage = ({ router }) => {
  const { loading, data } = useQuery(GET_PROGRAM, {
    variables: {
      data: { id: router.query.id },
    },
  });

  return (
    <>
      <Layout>{loading ? <Spinner /> : renderBody(data.program)}</Layout>
    </>
  );
};

export default withRouter(SchoolPage);
