import React, {useState} from 'react'

import {Container, Spacer, Button, ButtonContainer} from './BottomBar.styles'






const BottomBar = ({page, setPage, itemsPerPage}) => {

    const [active, setActive] = useState(1)

    const moveBack = () => {setPage(page - 1)}
    const moveForward = () => {setPage(page + 1)}

    const renderButtons = () => {
        const numberOfPages = Math.ceil(page / itemsPerPage)
        let buttons = []

        const numberOfButtons = active + 3 > numberOfPages ? numberOfPages - active : 3;

        for (
          let i = Math.min(active + 1, numberOfPages - 3);
          i <= Math.min(active + numberOfButtons, numberOfPages - 1);
          i++
        ) {
          if (i >= 1) {
            buttons.push(i);
          }
        }

        buttons[buttons.length - 1] === numberOfPages - 1 ? null : buttons.push(null);

        buttons.push(numberOfPages);
        buttons = buttons.map((pageNumber, i) =>
          pageNumber ? (
            <Button
              key={i}
              onClick={() => { setActive(i)} }
            >
              {pageNumber}
            </Button>
          ) : (
            <Spacer>...</Spacer>
          )
        );


        if(page !== 1){ buttons.unshift( <Button key={"prev"} onClick={ ()=>{moveBack()} } isEnd> {"< Prev"}</Button> ) }
        if(numberOfPages > 1 && active !== numberOfPages) { buttons.push( <Button key={"next"} onClick={ ()=>{moveForward()} } isEnd>{"Next >"}</Button>)   }

        return buttons
    }


    return(
        <Container>
            <ButtonContainer>
                {renderButtons()}
                {/* <Button isEnd> {"< Prev"}</Button>
                <Button>1</Button>
                <Button isActive>2</Button>
                <Button>3</Button>
                <Spacer>...</Spacer>
                <Button>27</Button>
                <Button isEnd>{"Next >"}</Button> */}
            </ButtonContainer>
        </Container>
    )
}

export default BottomBar
