import styled from 'styled-components'



type BannerType = {
    backgroundColor: string
}

type LogoType ={
    src: string
}

export const Logo = styled.img`
    background-image: url(${(props:LogoType)=>props.src});
    border-radius: 50%;
    background-size: fill;
    background-repeat: no-repeat;
    background-position: center;
    background-color: white;
`

export const Banner = styled.div`
display: flex;
flex-direction: column;
justify-content: center;
align-items: center;
    background-color: ${(props:BannerType)=>props.backgroundColor};
    padding: 3rem 0;
    color: white;
`

export const ProgramName = styled.h3`
    font-weight: bold;
    font-size: 2rem;
    padding: 1rem 0;
`

export const DegreeType = styled.h4`

`

export const CollegeName = styled.h4`
    font-size: 1.5rem;
`
