import gql from 'graphql-tag';

export const PROGRAM_SEARCH = gql`
    query programSearch($data: ProgramSearchTermInput) {
        programSearch(data: $data){
        count
        programs {
            id
            name
            degreeType
            deliveryMode
            requiredCredits
            costPerCredit
            school{
                name
                schoolLogo
                totalEnrollment
                schoolType
            }
        }
    }
}
`


export const GET_PROGRAM = gql`
    query program($data: ProgramInput){
        program(data: $data){
            id
            name
            costPerCredit
            programUrl
            programDescription
            degreeType
            requiredCredits
            deliveryMode
            school {
                name
                schoolLogo
                backgroundColor
                gradientColor
                schoolDescription
                schoolType
            }
        }
    }
`
