import React from 'react';

import { Container, MenuItem, MenuContainer } from './FilterMenu.styles';

import {useDispatch} from 'react-redux'
import {updateFilter} from '../../redux/Filter/filter.actions'
import {updateSort} from '../../redux/Sort/sort.actions'

const FilterMenu = ({exec}) => {
  const dispatch = useDispatch()

  return(
    <Container>
    <MenuContainer>
      Filter by: <MenuItem onClick={()=>{
        dispatch(updateFilter("Certificate"))
        exec()
        }}>Certificate</MenuItem> |
      <MenuItem onClick={()=>{
        dispatch(updateFilter("Bachelor's"))
        exec()
        }}>Bachlores</MenuItem> |
      <MenuItem onClick={()=>{dispatch(updateFilter("Master's"))}}>Masters</MenuItem> |
      <MenuItem onClick={()=>{dispatch(updateFilter("Ph.D."))}}>Ph.D.</MenuItem>
    </MenuContainer>
    <MenuContainer>
      Sort by: <MenuItem onClick={()=>{dispatch(updateSort("name"))}}>School Name</MenuItem> |
      <MenuItem onClick={()=>{dispatch(updateSort("high to low"))
      exec()
    }}>Credit Cost (Low to High)</MenuItem> |
      <MenuItem onClick={()=>{dispatch(updateSort("low to high"))}}>Credit Cost (High to Low)</MenuItem>
    </MenuContainer>
  </Container>
  )

}


export default FilterMenu;
