import React, {useRef, useEffect} from 'react'

import {Inner, Container} from './Spinner.styles'


export const Spinner = () => {
    let height = 0
    let width = 0
    const outer = useRef(null)

    useEffect(()=>{
        let box = outer
        height = box.current.clientHeight
        width = box.current.clientWidth
    },[outer, height, width])

    return (
        <Container ref={outer} >
            <Inner height={height} width={width}/>
        </Container>
    ) 

}
