import React from 'react';
import Head from 'next/head';
import { withRouter } from 'next/router';
import { useQuery } from '@apollo/react-hooks';
import { GET_PROGRAM } from '../../graphQL/queries';
import Layout from '../../components/Layout/Layout';
import { Spinner } from '../../components/Spinner/Spinner';

import SchoolBanner from '../../components/SchoolBanner/SchoolBanner';
import ProgramInfoBanner from '../../components/ProgramInfoBanner/ProgramInfoBanner';
import ProgramInfo from '../../components/ProgramInfo/ProgramInfo';

const calcDeliveryModeColor = (deliveryMode, color1, color2) => {
  const splitHex = hex =>
    hex.length === 4
      ? hex
          .slice(1, 4)
          .split('')
          .map(el => el + el)
      : [hex.substr(1, 2), hex.substr(3, 2), hex.substr(5, 2)];

  let splitColor1 = splitHex(color1).map(el => parseInt(el, 16));
  let splitColor2 = splitHex(color2).map(el => parseInt(el, 16));
  let num = deliveryMode.match(/d/)
  let percent = 100;

  let color = (
    splitColor1
      .map((el, i) =>{
        let c = Math.ceil((el + splitColor2[i]) * (1 - percent)).toString(16)
            return c === "0" ? "00" : c
      })
      .join('')
  );
  return color
};

const renderBody = ({
  name,
  costPerCredit,
  programUrl,
  programDescription,
  deliveryMode,
  school,
}) => {
  return (
    <div>
      <Head>
        <title>{name}</title>
      </Head>
      <SchoolBanner school={school} name={name} />
      <ProgramInfoBanner
        deliveryModeColor={calcDeliveryModeColor(deliveryMode,'#FF0000', '#0000FF')}
      />
      <ProgramInfo
        programDescription={programDescription}
        schoolDescription={school.schoolDescription}
        schoolName={school.name}
      />
    </div>
  );
};

const SchoolPage = ({ router }) => {
  const { loading, data } = useQuery(GET_PROGRAM, {
    variables: {
      data: { id: router.query.id },
    },
  });

  return (
    <>
      <Layout>{loading ? <Spinner /> : renderBody(data.program)}</Layout>
    </>
  );
};

export default withRouter(SchoolPage);
