import React from 'react';
import {useSelector, shallowEqual} from 'react-redux'

import { Wrapper, ResultsHeader } from './ProgramContainer.styles';
import ProgramRow from '../ProgramRow/ProgramRow';
import { faSatellite } from '@fortawesome/free-solid-svg-icons';

const renderPrograms = programs => {
  return programs.map(program => <ProgramRow key={program.id} program={program} />);
};

const renderHeader = (count, term) => term ? `${count} items found for ${term}` :`${count} items found` 

const selectPrograms = (state) => state.programs
const selectCount = (state) => state.count

const ProgramContainer = ({term=null}) => {

  const programs = useSelector(selectPrograms,  shallowEqual)
  const count = useSelector(selectCount, shallowEqual)

  return(
    <Wrapper>
          <ResultsHeader>{renderHeader(count, term)}</ResultsHeader> 
          {renderPrograms(programs)}
    </Wrapper>
    )
};

export default ProgramContainer;
