import React, {useEffect, useState} from 'react';
import axios from 'axios'
import Layout from '../../components/Layout/Layout'
import Header from '../../components/Header/Header'
import FilterMenu from '../../components/FilterMenu/FilterMenu'
import BottomBar from '../../components/BottomBar/BottomBar'
import ProgramRow from '../../components/ProgramRow/ProgramRow'
import {useLazyQuery} from '@apollo/react-hooks'
import {GET_COUNT, GET_ALL_PROGRAMS} from '../../graphQL/queries'

import { ProgramContainer, ResultsHeader} from './HomePage.styles'

type HomePageProps = {
  getInitialProps: Function
}

interface HomePageInterface<HomePageProps> extends React.FC<HomePageProps> {
  getInitialProps?: (ctx: any) => Promise<any>
}

const HomePage: HomePageInterface<HomePageProps> = () => {

  const [s, setS] = useState(false)
  const [getAllPrograms, {loading, data, fetchMore}] = useLazyQuery(GET_ALL_PROGRAMS, {
    variables: {
      offset: 0,
      limit: 10
    },
    fetchPolicy: "cache-and-network"
  })

  useEffect(()=>{
    getAllPrograms()
  }, [])

  if(data & data.allPrograms){
   console.log(data.allPrograms)
  }
  return (
    <Layout>
    <Header />
    <FilterMenu />
    <ProgramContainer>
        <ResultsHeader>300 programs found for engineering!</ResultsHeader>
        <ProgramRow />
        <ProgramRow />
        <ProgramRow />
        <ProgramRow />
        <ProgramRow />
        <ProgramRow />
        <ProgramRow />
        <ProgramRow />
        <ProgramRow />
        <ProgramRow />
    </ProgramContainer>
    <BottomBar/>
  </Layout>
  )
}

const getQuery = (QUERY, params?) => new Promise(async (res, rej)=> {
  const data:any = await axios({
    url: "http://localhost:4000/gql",
    method: "post",
    data: {
      query: GET_COUNT
    }
  })
  res(data)
})

HomePage.getInitialProps = async(ctx) => {

  //let resp:any = await getQuery(GET_COUNT)


  return {}
}


export default HomePage;
