import { FilterActionTypes } from "./filter.actions"

//TODO Setup the Filter Reducer

const initialState = ""

export default (state=initialState, action) => {
    switch(action.type){
        case FilterActionTypes.updateFilter: {
            return action.payload
        }
        default: return state
    }
}
