import React, {useEffect} from 'react';

import { useQuery } from '@apollo/react-hooks';

import Layout from '../../components/Layout/Layout'
import Header from '../../components/Header/Header'
import FilterMenu from '../../components/FilterMenu/FilterMenu'
import BottomBar from '../../components/BottomBar/BottomBar'
import ProgramRow from '../../components/ProgramRow/ProgramRow'

import {GET_COUNT} from '../../graphQL/queries'

import { ProgramContainer, ResultsHeader} from './HomePage.styles'

type HomePageProps = {
  getInitialProps: Function
}

interface HomePageInterface<HomePageProps> extends React.FC<HomePageProps> {
  getInitialProps?: (ctx: any) => Promise<any>
}

const HomePage: HomePageInterface<HomePageProps> = () => {



  return (
    <Layout>
    <Header />
    <FilterMenu />
    <ProgramContainer>
        <ResultsHeader>300 programs found for engineering!</ResultsHeader>
        <ProgramRow />
        <ProgramRow />
        <ProgramRow />
        <ProgramRow />
        <ProgramRow />
        <ProgramRow />
        <ProgramRow />
        <ProgramRow />
        <ProgramRow />
        <ProgramRow />
    </ProgramContainer>
    <BottomBar/>
  </Layout>
  )
}

const getData = () => new Promise((res, rej)=> {
  const { loading, error, data } = useQuery(GET_COUNT);
  if(!loading){
    console.log(data)
    res(data)
  }
})

HomePage.getInitialProps = async(ctx) => {

  await getData()
  return {}
}


export default HomePage;
