import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Layout from '../../components/Layout/Layout';
import FilterMenu from '../../components/FilterMenu/FilterMenu';
import BottomBar from '../../components/BottomBar/BottomBar';
import { useLazyQuery, useQuery } from '@apollo/react-hooks';
import { GET_COUNT, GET_ALL_PROGRAMS } from '../../graphQL/queries';
import { Spinner } from '../../components/Spinner/Spinner';

import ProgramContainer from '../../components/ProgramContainer/ProgramContainer';

type HomePageProps = {
  getInitialProps: Function;
};

interface HomePageInterface<HomePageProps> extends React.FC<HomePageProps> {
  getInitialProps?: (ctx: any) => Promise<any>;
}


const returnVariables = (page) => {
  const itemsPerPage = 10;
  return {
      offset: (page-1) * itemsPerPage,
      limit: itemsPerPage
    }
}

const HomePage: HomePageInterface<HomePageProps> = () => {
  const itemsPerPage = 10;
  const [page, setPage] = useState(1);
  const [programs, setPrograms] = useState([])

  const { loading, data, fetchMore } = useQuery(
    GET_ALL_PROGRAMS, {variables: returnVariables(page)}
  );

  if(!loading && programs.length === 0){
    setPrograms(data.allprograms.programs)
  }

  return (
    <Layout>
      <button onClick={()=>{
        fetchMore( {
          variables: {
            offset: (page -1) * itemsPerPage,
            limit: 10
          },
          updateQuery: (prev, {fetchMoreResult} ) => {
            let programs = fetchMoreResult.allPrograms.programs
            console.log(prev.allPrograms.programs.push(...programs))
          }
        }
        )


    }}>click</button>
        <FilterMenu />
      {
        loading ? <Spinner /> : 
        <ProgramContainer loading={loading}  count={data.allPrograms.count} programs={data.allPrograms.programs} term={null} />
      }
      <BottomBar />
    </Layout>
  );
};

export default HomePage;
