import React from 'react';
import Head from 'next/head';
import { withRouter } from 'next/router';
import { useQuery } from '@apollo/react-hooks';
import { GET_PROGRAM } from '../../graphQL/queries';
import Layout from '../../components/Layout/Layout';
import { Spinner } from '../../components/Spinner/Spinner';

import SchoolBanner from '../../components/SchoolBanner/SchoolBanner';
import ProgramInfoBanner from '../../components/ProgramInfoBanner/ProgramInfoBanner'
import ProgramInfo from '../../components/ProgramInfo/ProgramInfo'

const renderBody = ({
  name,
  costPerCredit,
  programUrl,
  programDescription,
  deliveryMode,
  school,
}) => {
  return (
    <div>
      <Head>
        <title>{name}</title>
      </Head>
      <SchoolBanner
        school={school}
        name={name}
        costPerCredit={costPerCredit}
        deliveryMode={deliveryMode}
      />
      <ProgramInfoBanner />
      <ProgramInfo programDescription={programDescription} schoolDescription={school.schoolDescription} />
    </div>
  );
};

const SchoolPage = ({ router }) => {
  const { loading, data } = useQuery(GET_PROGRAM, {
    variables: {
      data: { id: router.query.id },
    },
  });

  return (
    <>
      <Layout>{loading ? <Spinner /> : renderBody(data.program)}</Layout>
    </>
  );
};

export default withRouter(SchoolPage);
