import React, {useState} from 'react'

import { library } from "@fortawesome/fontawesome-svg-core";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faSearch } from "@fortawesome/free-solid-svg-icons";
library.add(faSearch);

import { useDispatch } from 'react-redux'
import { updateTerm } from '../../redux/Term/term.actions'
import {Container, Input, SubmitButton} from './SearchBar.styles'


/*
    TODO: Implment search functionality
*/



const SearchBar = () => {

    const [text, setText] = useState('')
    const dispatch = useDispatch()

    return(
        <Container>
            <Input
                value={text}
                onChange={(e)=>{setText(e.target.value)}}
                placeholder="Search for Online Course"
            />
            <SubmitButton onClick={()=>{dispatch( updateTerm(text)) }}>        
                <FontAwesomeIcon
                    style={{ color: "#FFFFFF" }}
                    icon={["fas", "search"]}
                    />{" "}
            </SubmitButton>
        </Container>
    )
}

export default SearchBar
