import React, { useState, useEffect } from 'react';
import Layout from '../../components/Layout/Layout';
import FilterMenu from '../../components/FilterMenu/FilterMenu';
import BottomBar from '../../components/BottomBar/BottomBar';
import { useQuery, useLazyQuery } from '@apollo/react-hooks';
import { PROGRAM_SEARCH } from '../../graphQL/queries';
import { Spinner } from '../../components/Spinner/Spinner';
import {useSelector, shallowEqual} from 'react-redux'
import { ResultsHeader , ProgramContainer} from './HomePage.styles'
import ProgramRow from '../../components/ProgramRow/ProgramRow';

//TODO: Build the home page

type HomePageProps = {
  getInitialProps: Function;
};

interface HomePageInterface<HomePageProps> extends React.FC<HomePageProps> {
  getInitialProps?: (ctx: any) => Promise<any>;
}



const renderProgramContainer = (programs, count, term=null) => {

  const renderPrograms = programs => {
    return programs.map((program, i) => <ProgramRow key={program.id + program.name + i} program={program} />);
  };

  const renderHeader = (count, term) => term ? `${count} items found for ${term}` :`${count} items found` 

  return (
    <ProgramContainer>
    <ResultsHeader>{renderHeader(count, term)}</ResultsHeader> 
        {renderPrograms(programs)}
    </ProgramContainer>
  )
}


const HomePage: HomePageInterface<HomePageProps> = () => {

  let count = 0
  let programs = []
  const itemsPerPage = 10;
  const [page, setPage] = useState(1);

  const term = useSelector((state:any) => state.term, shallowEqual )
  const sort = useSelector((state:any) => state.sort, shallowEqual )
  const filter = useSelector((state:any) => state.filter, shallowEqual )


  let variables = {
      data: {
        offset: (page-1) * itemsPerPage,
        limit: itemsPerPage,
        term, sort, filter
      }
    }

  const { loading, data } = useQuery( PROGRAM_SEARCH, { variables });

  if(!loading){
    count = data.programSearch.count
    programs = data.programSearch.programs
  }

  return (
    <Layout >
      <FilterMenu />
      { loading  ? <Spinner /> : renderProgramContainer(programs,count,term) }
      <BottomBar count={count} page={page} setPage={setPage} itemsPerPage={itemsPerPage}/>
    </Layout>
  );
};

export default HomePage;
