import gql from 'graphql-tag';


type ProgramSearchTermInput = {
    offset: Number,
    limit: Number,
    term: String
}

export const PROGRAM_SEARCH = gql`
    query programSearch($data: ProgramSearchTermInput) {
    allPrograms(data: $data){
        count
        programs {
            id
            name
            degreeType
            deliveryMode
            school{
                name
                schoolLogo
                totalEnrollment
                schoolType
            }
        }
    }
}
`

type ProgramInput = {
    id: String
}


export const GET_PROGRAM = gql`
    query program($data: ProgramInput){
        program(data: $data){
            id
            name
            costPerCredit
            programUrl
            programDescription
            deliveryMode
            school {
                name
                schoolLogo
                backgroundColor
                gradientColor
                schoolDescription
            }
        }
    }
`
