import styled from 'styled-components'

export const Container = styled.div`
    display: flex;
    flex-grow: 1;
    width: 100%;
    border: 1px solid #888888;
    border-radius: .2rem;
    overflow: hidden;
`

export const Input = styled.input`
    display: flex;
    flex-grow: 1;
    padding: .2rem 1rem;
    color: #888888;
    font-weight: 600;
`

export const SubmitButton = styled.button`
    height: auto;
    width: 1.5rem;
    border: none;
    background-color: #ffaa00;
`
