import styled from 'styled-components'

export const Container = styled.div`
    display: flex;
    justify-content: center;
    flex-grow: .8;
    border: 2px solid #bbb;
    border-radius: .2rem;
    overflow: hidden;
`

export const Input = styled.input`
    display: flex;
    flex-grow: 1;
    padding: .2rem 1rem;
    color: #888888;
    font-weight: 800;
    border: none;

    &,
    &:focus,
    &:active {
        outline: none;
    }

    &::placeholder{
        color: #dddddd;
    }
`

export const SubmitButton = styled.button`
    height: auto;
    width: 1.5rem;
    border: none;
    background-color: #ffaa00;
`
