import gql from 'graphql-tag';


type ProgramSearchInput = {

}

export const Program_Search = gql`
    query programSearch($offset: Float, $limit: Float) {
    allPrograms(offset: $offset, limit: $limit){
        count
        programs {
            id
            name
            degreeType
            deliveryMode
            school{
                name
                schoolLogo
                totalEnrollment
                schoolType
            }
        }
    }
}
`

type ProgramInput = {
    id: String
}


export const GET_PROGRAM = gql`
    query program($data: ProgramInput){
        program(data: $data){
            id
            name
            costPerCredit
            programUrl
            programDescription
            deliveryMode
            school {
                name
                schoolLogo
                backgroundColor
                gradientColor
                schoolDescription
            }
        }
    }
`
