  const dispatch = useDispatch()

  return(
    <Container>
    <MenuContainer>
      Filter by: <MenuItem onClick={ ()=>{dispatch(updateFilter("Cer"))} }>Certificate</MenuItem> |
      <MenuItem onClick={ ()=>{dispatch(updateFilter("Bach"))} }>Bachlores</MenuItem> |
      <MenuItem onClick={ ()=>{dispatch(updateFilter("Mas"))} }>Masters</MenuItem> |
      <MenuItem onClick={ ()=>{dispatch(updateFilter("Doc"))} }>Ph.D.</MenuItem> |
      <MenuItem onClick={ ()=>{dispatch(updateFilter(""))} }>All</MenuItem>
    </MenuContainer>
  </Container>
  )

}


export default FilterMenu;
