import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Layout from '../../components/Layout/Layout';
import FilterMenu from '../../components/FilterMenu/FilterMenu';
import BottomBar from '../../components/BottomBar/BottomBar';
import { useLazyQuery, useQuery } from '@apollo/react-hooks';
import { GET_COUNT, GET_ALL_PROGRAMS } from '../../graphQL/queries';
import { Spinner } from '../../components/Spinner/Spinner';

import ProgramContainer from '../../components/ProgramContainer/ProgramContainer';

type HomePageProps = {
  getInitialProps: Function;
};

interface HomePageInterface<HomePageProps> extends React.FC<HomePageProps> {
  getInitialProps?: (ctx: any) => Promise<any>;
}


const HomePage: HomePageInterface<HomePageProps> = () => {
  const itemsPerPage = 10;
  const [page, setPage] = useState(1);

 const { loading: loadingCount, data: count } = useQuery(GET_COUNT);
  const { loading: loadingPrograms, data: programs } = useQuery(
    GET_ALL_PROGRAMS, {
      variables: {
        offset: (page-1) * itemsPerPage,
        limit: itemsPerPage
      }
    }
  );


  return (
    <Layout>
        <FilterMenu />
      {loadingCount && loadingPrograms ? <Spinner /> : 
        <ProgramContainer  count={count.programsCount} programs={programs.allPrograms} term={null} />
      }
      <BottomBar />
    </Layout>
  );
};

export default HomePage;
