import React from 'react'

import {Container, Spacer, Button, ButtonContainer} from './BottomBar.styles'

/* 
    TODO: Render out buttons:

          There should be no more then 4 buttons displayed.

          If there are more then or equal to 4 pages, then the last numbered button should show
          the final page number.

          If there are less then 4 pages, only display the number of buttons
          necissary

          If the user is on the first page. Do not show the previous button.
          If the user is on the last page.  Do not show the next button.
          If the user is within the last 4 pages, Do not show the spacer.
*/



const renderButtons = (currentIndex, currentCount, displayTotal) => {
    const totalPages = Math.ceil(currentCount / displayTotal);

    const buttons = [];

    const numberOfButtons = currentIndex + 3 > totalPages ? totalPages - currentIndex : 3;

    for (
      let i = Math.min(currentIndex + 1, totalPages - 3);
      i <= Math.min(currentIndex + numberOfButtons, totalPages - 1);
      i++
    ) {
      if (i >= 1) {
        buttons.push(i);
      }
    }

    buttons[buttons.length - 1] === totalPages - 1 ? null : buttons.push(null);

    buttons.push(totalPages);
    return buttons.map((pageNumber, i) =>
      pageNumber ? (
        <Button
          key={i}
          onClick={() => { }}
        >
          {pageNumber}
        </Button>
      ) : (
        <Spacer>...</Spacer>
      )
    );
  };



const BottomBar = ({page, setPage, itemsPerPage}) => {

    const moveBack = () => {setPage(page - 1)}
    const moveForward = () => {setPage(page + 1)}

    const renderButtons = () => {
        const numberOfPages = Math.ceil(page / itemsPerPage)

        if(numberOfPages <= 1){
            return <Button isActive > 1</Button>
        }else{
            const buttons = []
            page !== 1 && buttons.push(<Button onClick={()=>{moveBack()}} isEnd> {"< Prev"}</Button>)



            page >= (numberOfPages - 4) && buttons.push(<Button onClick={()=>{moveForward()}} isEnd> {"Next >"}</Button>)
        }

    }


    return(
        <Container>
            <ButtonContainer>
                {renderButtons()}
                {/* <Button isEnd> {"< Prev"}</Button>
                <Button>1</Button>
                <Button isActive>2</Button>
                <Button>3</Button>
                <Spacer>...</Spacer>
                <Button>27</Button>
                <Button isEnd>{"Next >"}</Button> */}
            </ButtonContainer>
        </Container>
    )
}

export default BottomBar
